import{_ as n}from"./plugin-vue_export-helper-c27b6911.js";import{r as s,o as r,c as a,e as c,a as e,b as t,d as _,f as i}from"./app-c58985cf.js";const l="/assets/img/跑步心得/image-20210521195833720.png",p={},d=e("h1",{id:"初级跑者心得",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#初级跑者心得","aria-hidden":"true"},"#"),t(" 初级跑者心得")],-1),h=e("figure",null,[e("img",{src:l,alt:"相关图片",height:"300",tabindex:"0",loading:"lazy"}),e("figcaption",null,"相关图片")],-1),m=e("p",null,"跑步，大众话题。根据速度，运动时间段，运动方式不同，跑步也被分成了各种类别，轻松跑，强度跑，晨跑，下午跑，夜跑，间歇跑，变速跑等等，有些名字我乱编的，大概为了表达相对应的跑步习惯。",-1),g=e("strong",null,"对于专业跑步竞赛者",-1),u={href:"https://www.youtube.com/watch?v=m63Cwdubuf4&t=20s",target:"_blank",rel:"noopener noreferrer"},f=i("<p><strong>对于大众跑者</strong> 个人认为他们的跑步，是一种让自己保持清醒，健康的方式。他们的工作一般都是久坐，室内作业，如会计师，程序员等。那么这类触手可及的运动在缓解你精神压力的同时，也能够在一定程度上改善身体状况。跑步与其他有氧类运动相似，我个人喜欢跑步是因为他的灵活性。一个人想静静的时候可跑，寂寞了想约人亦可。这点有点像是游泳。</p><p><strong>坚持有规律的跑步只能是一小部分人的习惯</strong> 工作强度高的工人，有固定作息，但光是基础工作消耗的卡路里，是普通人一天总消耗的三到四倍。他们需要的更多的是心理上的缓解，打打游戏，看看电影，逛超市，逛公园。而非是使得他们更劳累的跑步。因此我不会推销跑步，因为他只是一个选择。甚至可以说，打工人牺牲了他们的体力和时间，推动了社会前进的一小步，而我却自私地将体力花费在了自己的跑步上。这种想法有点极端，却也一度改变了我的跑步思维。使我减少着每次跑步的时间，不断提升跑步的效率。</p><p><strong>跑步带来什么</strong> 最初的跑步只是为了跑步，每次跑完心中便有了一分踏实。相对枯燥的电脑屏幕和一个人安静的卧室，路上的一切无不让人陶醉，最享受的是家乡小镇上的花海谷跑个 10 公里。闻轻风听鸟语，有时还能看见穿着汉服的游人。当时跑步并无时间安排，没有限时，往往一跑便是一两小时。</p><p>而后渐渐意识到，自己的跑地有点奢侈。也可以说，戴着跑步的帽子，放肆的每天下午花一两小时去享受风景。于是，我开始渐渐地，将自己的跑步改为早上 8 点左右的 30 分钟 5 公里。将下午脑袋最清晰的黄金时间段，留给学习和思考。嗯对的，目前本人还是学生。这样一来，跑完步便能有经历充沛的一天，去做该做的事。</p><p><strong>工作后，还会跑嘛？</strong> 会的，庆幸的是我的工作并不是高强度的体力活。跑步更多的是为了清醒的思路，和一个能够让我坚持着把思路一步步落实的体魄。</p><p>跑步也锻炼了一个健康，坚持的心态。这种心态，也被我带入到工作中，生活中，去对待每一点琐碎，做好每一件事。</p>",6);function x(b,V){const o=s("ExternalLinkIcon");return r(),a("div",null,[d,h,c("more"),m,e("p",null,[g,t(" 毋庸置疑，他们需要有专业的，系统的训练方式。如 VDOT O2 发布在油管的这个"),e("a",u,[t("系列视频"),_(o)]),t("， 里面讲解到了马拉松选手需要注重的跑步速度，动作，生活习惯等。 因此，何时何地跑步，怎么去跑，在他们眼中便有了优劣之分。")]),f])}const N=n(p,[["render",x],["__file","跑步心得.html.vue"]]);export{N as default};
